const USMap = ({ data }) => {
	return <svg id='us-heat-map' style={{stroke: 'none'}} version='1.1' viewBox='150 30 830 530' xmlns='http://www.w3.org/2000/svg'>
		{Object.entries(mapPaths).map(([id, props]) => {
			let { carbon = 0, colour } = (data[id] || {});
			if (!carbon.toFixed) debugger;
			let unit = 'kg';
			if (carbon >= 1000) {
				carbon /= 1000;
				unit = 't';
			}
			return <path key={id} fill={colour} {...props}>
				<title>{props.name}: {carbon.toFixed(2)} {unit} CO2e</title>
			</path>
		})}
	</svg>;
};


const MapCard = ({ baseFilters }) => {
	const [mapData, setMapData] = useState({});

	const pvChartData = getCarbon({ ...baseFilters, breakdown: ['locn_sub1'] });
	useEffect(() => {
		if (!pvChartData.value) return;
		const locnTable = pvChartData.value.tables.locn_sub1.slice(1);
		const stateRows = locnTable.filter(row => mapPaths[row[0]]);
		const nonStateRows = locnTable.filter(row => nonStates.includes(row[0]));
		const otherRows =  locnTable.filter(row => !mapPaths[row[0]] && !nonStates.includes(row[0]));
		// remove items whose location doesn't correspond to a US state
		const processedRows = [
			...stateRows,
			nonStateRows.reduce((acc, row) => (
				[acc[0], acc[1] + row[1], acc[2] + row[2]]
			), ['TERR', 0, 0]),
			otherRows.reduce((acc, row) => (
				[acc[0], acc[1] + row[1], acc[2] + row[2]]
			), ['OTHER', 0, 0]),
		]
		
		// assign colours
		const colours = dataColours(processedRows.map(row => row[2]));
		// zip colours, states, carbon together for the map
		setMapData(processedRows.reduce((acc, row, i) => {
			acc[row[0]] = { colour: colours[i], carbon: row[2] };
			return acc;
		}, {}));
	}, [pvChartData.value]);
	
	return (
		<GreenCard title='HERE IS A MAP OF THE U.S.' className='carbon-cta flex-column'>
			<USMap data={mapData} />
		</GreenCard>
	);
};

export default MapCard;
